INSERT INTO `t_blog` VALUES ('8', '', '', '<div class=\"postBody\">\r\n<div id=\"cnblogs_post_body\" class=\"blogpost-body cnblogs-markdown\">\r\n    <div class=\"clipboard-button\" id=\"copy_btn_0 \" data-clipboard-target=\"#copy_target_0\" title=\"复制代码\" style=\"padding: 4px; border-radius: 2px; text-align: right; user-select: none;\">复制代码</div><pre id=\"copy_target_0\"><code class=\"hljs cpp\"><span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;iostream&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;sstream&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;algorithm&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;cstdio&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;math.h&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;set&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;vector&gt;</span></span>\r\n<span class=\"hljs-meta\">#<span class=\"hljs-meta-keyword\">include</span><span class=\"hljs-meta-string\">&lt;string.h&gt;</span></span>\r\n<span class=\"hljs-keyword\">using</span> <span class=\"hljs-keyword\">namespace</span> <span class=\"hljs-built_in\">std</span>;\r\n<span class=\"hljs-keyword\">const</span> <span class=\"hljs-keyword\">int</span> maxn=<span class=\"hljs-number\">1e5</span>+<span class=\"hljs-number\">10</span>;\r\n<span class=\"hljs-keyword\">typedef</span> <span class=\"hljs-keyword\">long</span> <span class=\"hljs-keyword\">long</span> ll;\r\n \r\n<span class=\"hljs-function\"><span class=\"hljs-keyword\">int</span> <span class=\"hljs-title\">main</span><span class=\"hljs-params\">()</span>\r\n</span>{\r\n	<span class=\"hljs-keyword\">int</span> n;\r\n	<span class=\"hljs-built_in\">cin</span>&gt;&gt;n;\r\n  \r\n	<span class=\"hljs-keyword\">char</span> s;\r\n	<span class=\"hljs-built_in\">cin</span>&gt;&gt;s;\r\n	getchar();    <span class=\"hljs-comment\">//gerchar()我也不知道什么时候加  ，不对的时候加一下</span>\r\n<span class=\"hljs-comment\">//	char s1[11500]; </span>\r\n<span class=\"hljs-comment\">//	cin.get(s1,11500);  //char *是cin.get(string,string.length());</span>\r\n	<span class=\"hljs-built_in\">string</span> s1;\r\n	getline(<span class=\"hljs-built_in\">cin</span>,s1);   <span class=\"hljs-comment\">// getline is  string 对象 （cin,s1)</span>\r\n<span class=\"hljs-comment\">//	</span>\r\n<span class=\"hljs-comment\">//	int len = strlen(s1);</span>\r\n<span class=\"hljs-comment\">//	</span>\r\n	\r\n<span class=\"hljs-comment\">//	int c = n-len;</span>\r\n	<span class=\"hljs-keyword\">int</span> c = n - s1.length();\r\n	<span class=\"hljs-keyword\">if</span>(c &gt; <span class=\"hljs-number\">0</span>){\r\n		\r\n		<span class=\"hljs-keyword\">for</span>(<span class=\"hljs-keyword\">int</span> i = <span class=\"hljs-number\">0</span> ; i &lt; c ; i++){\r\n		<span class=\"hljs-built_in\">cout</span>&lt;&lt;s; \r\n	}\r\n	\r\n	<span class=\"hljs-built_in\">cout</span>&lt;&lt;s1;\r\n	\r\n}\r\n <span class=\"hljs-keyword\">else</span>\r\n \r\n {  \r\n    <span class=\"hljs-keyword\">int</span> c1=s1.length() - n;\r\n	\r\n	<span class=\"hljs-keyword\">for</span>(<span class=\"hljs-keyword\">int</span> i = c1; i &lt; s1.length()  ; i++){\r\n		\r\n		<span class=\"hljs-built_in\">cout</span>&lt;&lt;s1[i];\r\n	} \r\n}\r\n	\r\n	\r\n}\r\n</code></pre>\r\n\r\n</div>\r\n<div id=\"MySignature\"></div>\r\n<div class=\"clear\"></div>\r\n<div id=\"blog_post_info_block\"><div id=\"BlogPostCategory\">\r\n    分类: \r\n            <a href=\"https://www.cnblogs.com/shenxiaodou/c